import { ADD_FAVORITES, REMOVE_FAVORITE, FILTER, ORDER } from "./actions";

const initialState = {
  myFavorites: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAVORITES:
      return { ...state, myFavorites: [...state.myFavorites, action.payload] };//copiamos el estado(...state) y a myFav le damos una copia de lo q teniamos y agregamos el payload
    case REMOVE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
    case FILTER:
      return { ...state }
    case ORDER:
      return {}
    default:
      return {
        ...state,
      };
  }
};

export default reducer;
