import { useSelector, useDispatch } from "react-redux";
import style from "./Favorites.module.css";
import { Link } from "react-router-dom";
import { orderCards, filterCards } from "../Redux/actions";
import { useState } from "react";

const Favorites = () => {
  const favorites = useSelector((state) => state.myFavorites);
  const dispatch = useDispatch();
  const [aux, setAux] = useState(false);

  const handlerOrder = (event) => {
    dispatch(orderCards(event.target.value));
    setAux(true);
  };

  const handlerFilter = (event) => {
    dispatch(filterCards(event.target.value));
  };

  const handleFavorite = (isFav,setIsFav) => {
    if (isFav) {
      setIsFav(false);
      removeFavorite(id);
    } else {
      setIsFav(true);
      addFavorites({ name, species, gender, image });
    }
  };

  return (
    <div className={style.Container}>
      <div className={style.Filtros}>
        <div className={style.Selector}>
          <label>Order By</label>
          <select onChange={handlerOrder} className={style.SelectorOrder}>
            <option value="Ascendente">Asc</option>
            <option value="Descendente">Des</option>
          </select>
        </div>
        <div className={style.Selector}>
          <label>Gender</label>
          <select onChange={handlerFilter} className={style.SelectorOrder}>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
            <option value="allCharacters">all Characters</option>
          </select>
        </div>
      </div>
      {favorites.map((character) => {
        return (
          <div className={style.Cards}>
            {isFav ? (
              <button onClick={handleFavorite} className={style.Corazon}>
                ‚ù§Ô∏è
              </button>
            ) : (
              <button onClick={handleFavorite} className={style.Corazon}>
                ü§ç
              </button>
            )}
            <img src={character.image} alt={character.name} />
            <Link to={`/detail/${character.id}`}>
              <h2 className={style.Name}>{character.name}</h2>
            </Link>
            <h2 className={style.Especie}>{character.species}</h2>
            <h2 className={style.Especie}>{character.gender}</h2>
          </div>
        );
      })}
    </div>
  );
};

export default Favorites;
