import { ADD_FAVORITES, REMOVE_FAVORITE, FILTER, ORDER } from "./actions";

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAVORITES:
      return {
        ...state,
        myFavorites: [...state.myFavorites, action.payload],
        // allCharacters: [...state.myFavorites],
      }; //copiamos el estado(...state) y a myFav le damos una copia de lo q teniamos y agregamos el payload
    case REMOVE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
    case FILTER:
      const allCharactersFiltered = state.allCharacters.filter(
        (char) => char.gender === action.payload
      );
      return { ...state, myFavorites: allCharactersFiltered };
    case ORDER:
      return { ...state };
      action.payload === "asc" ? allCharacters.sort((a, b) => a.id < b.id) : allCharacters.sort((a, b) => a.id > b.id)

      const allCharactersSort = allCharacters.sort(a,b)
    default:
      return {
        ...state,
      };
  }
};

export default reducer;
